import React from "react";
class CreateEmployee extends React.Component{
    constructor(){
        super();
        this.state={
            id:"",
            name:"",
            age:"",
            adress:"",
            designation:"",
            employee:[]
        }
    }

    onIdChnage(event){
        this.setState({id:event.target.value})
    }
    onNameChnage(event){
        this.setState({name:event.target.value})
    }

    saveData(){
        let employee = this.state.employee;
        this.setState({employee:employee})
    }

    render(){
        return(
            <div><h1>Employee Create Form</h1>
                <form>
                    <label>Id : </label>
                    <input type="text" name="id" value={this.state.id} onChange={this.onIdChange}/>
                    <br></br>
                    <br></br>
                    <label>Name : </label>
                    <input type="text" name="name" value={this.state.name} onChange={this.onNameChange}/>
                    <br></br>
                    <br></br>
                    <label>Age : </label>
            
                    <input type="text" name="name" value={this.state.name} onChange={this.onAgeChange}/>
                    <input type="text" name="age" value={this.state.age} />
                    <br></br>
                    <br></br>
                    <label>Address : </label>
                    <input type="text" name="address" value={this.state.address} onChange={this.onAddressChange}/>
                    <br></br>
                    <br></br>
                    <label>Designation:</label>
                    <input type="text" name="desig" value={this.state.designation} onChange={this.onDesigChange}/>
                    <br></br>
                    <br></br>
                   <button onClick={this.saveData.bind(this)}>Save Data</button>


                </form>
            </div>
        )
    }
}

export default CreateEmployee
