import React from "react";

class ToDo extends React.Component{
    constructor(){
        super()
        this.state = {
            tasks:[],
            taskName:"",
        }
        handleChange=(event)=>{
            this.setState({taskName:event.target.value})
        }
    }
    render(){
        return(
            <div>
                <h2>My First ToDo Application</h2>
               <input type="text" placeholder="enter task name" onChange={this.handleChange} value={this.state.taskName}/>
               <button >Save Task</button>
            </div>
        )
    }
}

export default ToDo
