import React from "react";

class ToDo extends React.Component{
    constructor(){
        super()
        this.state = {
            tasks:[],
            taskName:"",
        }
    }
    render(){
        return(
            <div>
                <h2>My First ToDo Application</h2>
               <input type="text" placeholder="enter task name" value={this.state.taskName}/>
               <button >Save Task</button>
            </div>
        )
    }
}

export default ToDo
