    const [age,setAge] = userState("");
    const [address,setAddress] = useState("");

    function handleFnameChange(event){
        setFname(event.target.value);
    }

    function handleLnameChnage(event){
        setLname(event.target.value);
    }

    function handleAgeChnage(event){
        setAge(event.target.value);
    }
    function handleAddressChnage(event){
        setAddress(event.target.value);
    }

    function saveData(event){
        event.preventDefault();
        console.log("first name==",fname);
    }
    return(
        <form onSubmit={saveData}>
            <label>First Name:</label>
            <input type="text" value={fname} onChange={handleFnameChange}/>
            <input type="submit" value="save"/>
            <label>Last Name</label>
            <input type="text" value={lname} onChange={handleLnameChnage}/>
            <label>Age:</label>
            <input type="text" value={age} onChnage={handleAgeChnage}/>
            <label>Address: </label>
            <input type="text" value={address} onChange={handleAddressChnage}/>

        </form>
    )
}

export default FormByFunction
